  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}><MDXTag name="h1" components={components} props={{"id":"troubleshooting"}}><MDXTag name="a" components={components} parentName="h1" props={{"aria-hidden":true,"href":"#troubleshooting"}}><MDXTag name="span" components={components} parentName="a" props={{"className":"icon-link"}}>{`#`}</MDXTag></MDXTag>{`Troubleshooting`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"share-remote-pdf-file-with-gmail--whatsapp-ios"}}><MDXTag name="a" components={components} parentName="h2" props={{"aria-hidden":true,"href":"#share-remote-pdf-file-with-gmail--whatsapp-ios"}}><MDXTag name="span" components={components} parentName="a" props={{"className":"icon-link"}}>{`#`}</MDXTag></MDXTag>{`Share Remote PDF File with Gmail & WhatsApp (iOS)`}</MDXTag>
<MDXTag name="p" components={components}>{`When sharing a pdf file with base64, there are two current problems.`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`On WhatsApp base64 wont be recognized => nothing to share`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`In the GmailApp the file extension is wrong (.dat).`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`Therefore we use this "workaround" in order to handle pdf sharing for iOS Apps to mentioned Apps`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Install react-native-fetch-blob`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Set a specific path in the RNFetchBlob configurations`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Download the PDF file to temp device storage`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Share the response's path() of the donwloaded file directly`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`Code:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre">{`static sharePDFWithIOS(fileUrl, type) {
  let filePath = null;
  let file_url_length = fileUrl.length;
  const configOptions = {
    fileCache: true,
    path:
      DIRS.DocumentDir + (type === 'application/pdf' ? '/SomeFileName.pdf' : '/SomeFileName.png') // no difference when using jpeg / jpg / png /
  };
  RNFetchBlob.config(configOptions)
    .fetch('GET', fileUrl)
    .then(async resp => {
      filePath = resp.path();
      let options = {
        type: type,
        url: filePath // (Platform.OS === 'android' ? 'file://' + filePath)
      };
      await Share.open(options);
      // remove the image or pdf from device's storage
      await RNFS.unlink(filePath);
    });
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Nothing to do on Android. You can share the pdf file with base64`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre">{`static sharePDFWithAndroid(fileUrl, type) {
  let filePath = null;
  let file_url_length = fileUrl.length;
  const configOptions = { fileCache: true };
  RNFetchBlob.config(configOptions)
    .fetch('GET', fileUrl)
    .then(resp => {
      filePath = resp.path();
      return resp.readFile('base64');
    })
    .then(async base64Data => {
      base64Data = \`data:\${type};base64,\` + base64Data;
      await Share.open({ url: base64Data });
      // remove the image or pdf from device's storage
      await RNFS.unlink(filePath);
    });
}
`}</MDXTag></MDXTag></MDXTag>
